  events.forEach(event => {
    icsContent += 'BEGIN:VEVENT\n';
    icsContent += `UID:${event.uid}\n`;
    icsContent += `DTSTAMP:${new Date().toISOString().replace(/[-:]|\.\d{3}/g, '')}\n`;
    icsContent += `DTSTART:${event.dtstart}\n`;
    icsContent += `DTEND:${event.dtend}\n`;
    icsContent += `SUMMARY:${event.summary}\n`;
    icsContent += `DESCRIPTION:${event.description}\n`;
    icsContent += `LOCATION:${event.location}\n`;
    icsContent += `ORGANIZER:${event.organizer}\n`;
    icsContent += `ATTENDEE:${event.attendee}\n`;
    icsContent += 'END:VEVENT\n';
  });

  icsContent += 'END:VCALENDAR';

  const blob = new Blob([icsContent], { type: 'text/calendar;charset=utf-8' });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = 'trip-itinerary.ics';
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}
