const generateAndSendCalendarInvite = async (userEmail) => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!userEmail || !emailRegex.test(userEmail)) {
    alert('Please provide a valid email address');
    return;
  }

  const events = [
    {
      uid: 'evt1_bhimtal@travel.com',
      start: '20251025T093000Z',
      end: '20251025T113000Z',
      summary: 'Visit Bhimtal Lake',
      description: 'Enjoy a boat ride and visit the Island Aquarium in the serene Bhimtal Lake.',
      location: 'Bhimtal Lake, Bhimtal, Uttarakhand, India'
    },
    {
      uid: 'evt2_naukuchiatal@travel.com',
      start: '20251026T033000Z',
      end: '20251026T073000Z',
      summary: 'Explore Naukuchiatal',
      description: 'Adventure activities at the 'lake of nine corners'.',
      location: 'Naukuchiatal, Uttarakhand, India'
    },
    {
      uid: 'evt3_victoria_dam@travel.com',
      start: '20251026T093000Z',
      end: '20251026T103000Z',
      summary: 'Visit Victoria Dam',
      description: 'Scenic views from the historic dam.',
      location: 'Victoria Dam, Bhimtal, Uttarakhand, India'
    },
    {
      uid: 'evt4_hanuman_garhi@travel.com',
      start: '20251026T110000Z',
      end: '20251026T123000Z',
      summary: 'Sunset at Hanuman Garhi Temple',
      description: 'Spiritual visit and panoramic sunset views.',
      location: 'Hanuman Garhi Temple, Nainital, Uttarakhand, India'
    },
    {
      uid: 'evt5_hidimba_parvat@travel.com',
      start: '20251027T033000Z',
      end: '20251027T060000Z',
      summary: 'Hike Hidimba Parvat',
      description: 'Morning hike for stunning views.',
      location: 'Hidimba Parvat, Bhimtal, Uttarakhand, India'
    },
    {
      uid: 'evt6_sattal@travel.com',
      start: '20251027T100000Z',
      end: '20251027T130000Z',
      summary: 'Birdwatching at Sattal',
      description: 'Explore the cluster of seven freshwater lakes.',
      location: 'Sattal, Uttarakhand, India'
    }
  ];

  let icsContent = 'BEGIN:VCALENDAR\r\n';
  icsContent += 'VERSION:2.0\r\n';
  icsContent += 'PRODID:-//Travel Itinerary//EN\r\n';
  icsContent += 'METHOD:REQUEST\r\n';
  icsContent += 'CALSCALE:GREGORIAN\r\n';

  events.forEach(event => {
    icsContent += 'BEGIN:VEVENT\r\n';
    icsContent += 'UID:' + event.uid + '\r\n';
    icsContent += 'DTSTART:' + event.start + '\r\n';
    icsContent += 'DTEND:' + event.end + '\r\n';
    icsContent += 'SUMMARY:' + event.summary + '\r\n';
    icsContent += 'DESCRIPTION:' + event.description + '\r\n';
    icsContent += 'LOCATION:' + event.location + '\r\n';
    icsContent += 'ORGANIZER:mailto:support@travelplanner.com\r\n';
    icsContent += 'ATTENDEE;RSVP=TRUE;CN=' + userEmail + ':mailto:' + userEmail + '\r\n';
    icsContent += 'STATUS:CONFIRMED\r\n';
    icsContent += 'END:VEVENT\r\n';
  });

  icsContent += 'END:VCALENDAR';

  try {
    await sendCalendarEmail(userEmail, icsContent);
    alert('Calendar invitation sent successfully!');
  } catch (error) {
    console.error('Failed to send invitation:', error);
    alert('Failed to send invitation. Please try again.');
  }
};

